mapper.delete:

delete from order_info
where oi_id = #{oiId,jdbcType=INTEGER}
  and coms = #{coms,jdbcType=INTEGER}
  and comprice = #{comprice,jdbcType=DECIMAL}

mapper.update:

update order_info
set comid = #{comid,jdbcType=INTEGER},
  o_id = #{oId,jdbcType=INTEGER}
where oi_id = #{oiId,jdbcType=INTEGER}
  and coms = #{coms,jdbcType=INTEGER}
  and comprice = #{comprice,jdbcType=DECIMAL}

mapper.select:

select oi_id, coms, comprice, comid, o_id
from order_info
where oi_id = #{oiId,jdbcType=INTEGER}
  and coms = #{coms,jdbcType=INTEGER}
  and comprice = #{comprice,jdbcType=DECIMAL}
